import styled from 'styled-components/macro'; //gives name of component in dev tools

export const Inner = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    max-width: 1100px;
    margin: auto;
    flex-direction: ${({ direction }) => direction};
    width: 100%;

    @media (max-width: 1000px){
        flex-direction: column;

    }
  
`;

export const Pane = styled.div`
    width = 50%; 
    @media (max-width: 1000px) {
        width: 100%;
        padding: 0 45px;
        text-align: center;
      }
`;

export const Title = styled.h1`
    font-size: 50px;
    line-height: 1.1;
    margin-bottom: 8px;
    @media (max-width: 600px) {
      font-size: 35px;
    }
    
`;

export const SubTitle = styled.h2`
    font-size: 26px;
    font-weight: normal;
    line-height: normal;
    @media (max-width: 600px) {
      font-size: 18px;
    }
`;

export const Image = styled.img`
    max-width: 100%;
    height: auto;
`;


export const Container = styled.div``;
