import React, {useState, useContext, useEffect} from 'react';
import { SelectProfileContainer } from './profiles';
import { FirebaseContext } from '../context/firebase';
import * as ROUTES from '../constants/routes';
import logo from '../logo.svg';
import {Header, Loading} from '../components';

export function BrowseContainer({slides}){
    const [profile, setProfile] = useState({});
    const [loading, setLoading] = useState(true);

    const {firebase} = useContext(FirebaseContext);
    
    const user = firebase.auth().currentUser || {};

    useEffect(() => {
        setTimeout(() => {
          setLoading(false);
        }, 3000);
    }, [profile.displayName]);

    return profile.displayName ? (
        <>
            {loading ? (
                <Loading src= {user.photoURL}/>
            ) : (
                <Loading.ReleaseBody />
            )}
            <Header src="deadpool">
                <Header.Frame>
                    <Header.Logo to={ROUTES.HOME} src={logo} alt="Netflix" />
                    <Header.TextLink>Series</Header.TextLink>
                    <Header.TextLink>Films</Header.TextLink>
                </Header.Frame>
                <Header.Feature>
                    <Header.FeatureCallOut>Watch Deadpool 2</Header.FeatureCallOut>
                    <Header.Text>
                        Deadpool protects a young mutant Russell from the authorities and gets thrown in prison. However, he escapes and forms a team of mutants to prevent a time-travellign mercenary from killing Russell.
                    </Header.Text>
                </Header.Feature>
            </Header>
        </>
        
    ) : (
        <SelectProfileContainer user = {user} setProfile={setProfile}/>

    );

}
